// 递归翻译函数
function translateText(text, lang) {
  if (typeof text !== 'string') return text
  if (!translations[lang]) return text
  
  const langTranslations = translations[lang]
  
  // 先尝试精确匹配
  if (langTranslations[text]) {
    return langTranslations[text]
  }
  
  // 尝试部分匹配（用于FAQ答案等可能有变化的文本）
  for (const [en, translated] of Object.entries(langTranslations)) {
    if (text.includes(en) && en.length > 20) {
      return text.replace(en, translated)
    }
  }
  
  return text
}

// 递归处理对象
function translateObject(obj, lang) {
  if (Array.isArray(obj)) {
    return obj.map(item => translateObject(item, lang))
  } else if (obj !== null && typeof obj === 'object') {
    const translated = {}
    for (const [key, value] of Object.entries(obj)) {
      // 跳过不需要翻译的字段
      if (key === 'icon' || key === 'iconType' || key === 'in_menu' || key === 'sectionsOrder') {
        translated[key] = value
      } else if (key === 'metadata' && typeof value === 'object') {
        // metadata需要翻译description和title
        translated[key] = {
          ...value,
          title: translateText(value.title, lang),
          description: translateText(value.description, lang)
        }
      } else {
        translated[key] = translateObject(value, lang)
      }
    }
    return translated
  } else if (typeof obj === 'string') {
    return translateText(obj, lang)
  }
  return obj
}

// 处理单个文件
function processFile(filePath, lang) {
  try {
    const content = fs.readFileSync(filePath, 'utf8')
    const data = JSON.parse(content)
    
    // 只处理 compress-png 部分
    if (data['compress-png']) {
      data['compress-png'] = translateObject(data['compress-png'], lang)
      fs.writeFileSync(filePath, JSON.stringify(data, null, 2) + '\n', 'utf8')
      return true
    }
    return false
  } catch (error) {
    console.error(`Error processing ${filePath}:`, error.message)
    return false
  }
}

// 主函数
function main() {
  const dataDir = path.join(__dirname, '../src/data')
  const languages = ['de', 'ja', 'es', 'fr', 'ko', 'pt', 'zh-TW'] // 处理所有其他语言
  let totalProcessed = 0
  
  for (const lang of languages) {
    const filePath = path.join(dataDir, lang, 'image-compression.json')
    if (fs.existsSync(filePath)) {
      if (processFile(filePath, lang)) {
        totalProcessed++
        console.log(`✅ Processed: ${lang}/image-compression.json`)
      }
    }
  }
  
  console.log(`\n完成！處理了 ${totalProcessed} 個語言文件`)
}

main()
